import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import TypewriterBanner from './TypewriterBanner';
import ChapterResults from './ChapterResults';
import { getSubjectChapters } from '../data/subjectsData';

const Quiz = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [language, setLanguage] = useState('en'); // 'en' for English, 'ar' for Arabic
  const [currentChapter, setCurrentChapter] = useState('');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [answeredQuestions, setAnsweredQuestions] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showChapterResults, setShowChapterResults] = useState(false);
  const [selectedSubject, setSelectedSubject] = useState('');
  const [answeredQuestionsMap, setAnsweredQuestionsMap] = useState({}); // Track which questions have been answered
  const navigate = useNavigate();

  // Get the appropriate quiz data based on language and selected subject
  const currentQuizData = selectedSubject ? getSubjectChapters(selectedSubject, language) : {};

  // Set initial chapter when component mounts
  useEffect(() => {
    // Check for user preference in localStorage
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setDarkMode(savedDarkMode);

    // Apply dark mode to document if needed
    if (savedDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }

    // Check for language preference in localStorage
    const savedLanguage = localStorage.getItem('language');
    if (savedLanguage) {
      setLanguage(savedLanguage);
    }

    // Check for selected subject in localStorage
    const savedSubject = localStorage.getItem('selectedSubject');
    if (savedSubject) {
      setSelectedSubject(savedSubject);
    } else {
      // If no subject is selected, redirect to subject selection page
      navigate('/');
      return;
    }

    // Check if there's a selected chapter in localStorage
    const savedChapter = localStorage.getItem('selectedChapter');

    if (savedChapter) {
      // If coming from home page with a selected chapter
      setCurrentChapter(savedChapter);
      // Clear the selected chapter from localStorage
      localStorage.removeItem('selectedChapter');
    } else {
      // Redirect to chapters page if no chapter is selected
      navigate('/chapters');
    }
  }, [navigate]);

  // This effect handles language changes and maintains the current chapter and question
  useEffect(() => {
    if (currentChapter && selectedSubject) {
      // When switching languages, maintain the current chapter by mapping between languages
      let mappedChapter = currentChapter;

      // Only update if the chapter exists in the current language
      if (mappedChapter in currentQuizData) {
        setCurrentChapter(mappedChapter);

        // Try to find the equivalent question in the new language
        const savedQuestionText = localStorage.getItem('currentQuestionText');
        if (savedQuestionText) {
          const newQuestions = currentQuizData[mappedChapter];
          const newQuestionIndex = newQuestions.findIndex(q => q.question === savedQuestionText);
          if (newQuestionIndex !== -1) {
            setCurrentQuestionIndex(newQuestionIndex);
          } else {
            setCurrentQuestionIndex(0); // Fallback to first question if not found
          }
          localStorage.removeItem('currentQuestionText'); // Clear the stored text
        }
      }
    }
  }, [language, currentQuizData, currentChapter, selectedSubject]);

  // Update selectedOption when navigating to a previously answered question
  useEffect(() => {
    const questionKey = `${currentChapter}-${currentQuestionIndex}`;
    if (answeredQuestionsMap[questionKey] !== undefined) {
      setSelectedOption(answeredQuestionsMap[questionKey]);
      setShowExplanation(true); // Show explanation for previously answered questions
    } else {
      setSelectedOption(null);
      setShowExplanation(false);
    }
  }, [currentQuestionIndex, currentChapter, answeredQuestionsMap]);

  const chapters = Object.keys(currentQuizData);
  const currentQuestions = currentChapter && currentQuizData[currentChapter] ? currentQuizData[currentChapter] : [];
  const currentQuestion = currentQuestions.length > 0 ? currentQuestions[currentQuestionIndex] : null;

  // Check if current question has been answered
  const questionKey = `${currentChapter}-${currentQuestionIndex}`;
  const isQuestionAnswered = answeredQuestionsMap[questionKey] !== undefined;

  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());

    if (newDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  const toggleLanguage = () => {
    // Store the current question's text before changing language
    if (currentQuestion) {
      localStorage.setItem('currentQuestionText', currentQuestion.question);
    }

    const newLanguage = language === 'en' ? 'ar' : 'en';

    // Update language
    setLanguage(newLanguage);
    localStorage.setItem('language', newLanguage);
  };

  const handleOptionSelect = (optionIndex) => {
    // Prevent changing answer if question has already been answered
    if (isQuestionAnswered || !currentQuestion) return;

    setSelectedOption(optionIndex);

    // Record user's answer
    const isCorrect = optionIndex === currentQuestion.correct;
    if (isCorrect) {
      setScore(score + 1);
    }

    // Mark this question as answered
    setAnsweredQuestionsMap(prev => ({
      ...prev,
      [questionKey]: optionIndex
    }));

    setUserAnswers([...userAnswers, {
      question: currentQuestion.question,
      options: currentQuestion.options,
      userAnswer: optionIndex,
      correctAnswer: currentQuestion.correct,
      explanation: currentQuestion.explanation,
      isCorrect
    }]);

    setAnsweredQuestions(answeredQuestions + 1);
  };

  const handleNextQuestion = () => {
    if (!currentQuestion) return;

    if (currentQuestionIndex < currentQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // Chapter completed - show chapter results
      setShowChapterResults(true);
    }
  };

  const handlePrevQuestion = () => {
    if (!currentQuestion) return;

    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const continueToNextChapter = () => {
    // Find next chapter with questions
    let nextChapterFound = false;
    const currentChapterIndex = chapters.indexOf(currentChapter);

    for (let i = currentChapterIndex + 1; i < chapters.length; i++) {
      if (currentQuizData[chapters[i]] && currentQuizData[chapters[i]].length > 0) {
        setCurrentChapter(chapters[i]);
        setCurrentQuestionIndex(0);
        setSelectedOption(null);
        setShowExplanation(false);
        setUserAnswers([]);
        setScore(0);
        setShowChapterResults(false);
        setAnsweredQuestionsMap({}); // Reset answered questions for new chapter
        nextChapterFound = true;
        break;
      }
    }

    if (!nextChapterFound) {
      // All chapters completed
      setQuizCompleted(true);
      setShowResults(true);
    }
  };

  const goToChapters = () => {
    navigate('/chapters');
  };

  const restartQuiz = () => {
    // Find first chapter with questions
    let firstChapterWithQuestions = chapters[0];
    for (let i = 0; i < chapters.length; i++) {
      if (currentQuizData[chapters[i]] && currentQuizData[chapters[i]].length > 0) {
        firstChapterWithQuestions = chapters[i];
        break;
      }
    }

    setCurrentChapter(firstChapterWithQuestions);
    setCurrentQuestionIndex(0);
    setSelectedOption(null);
    setShowExplanation(false);
    setScore(0);
    setQuizCompleted(false);
    setShowResults(false);
    setAnsweredQuestions(0);
    setUserAnswers([]);
    setShowChapterResults(false);
    setAnsweredQuestionsMap({}); // Reset all answered questions
  };

  // Translations
  const translations = {
    en: {
      title: 'Computer Architecture Quiz',
      question: 'Question',
      of: 'of',
      correct: 'Correct!',
      incorrect: 'Incorrect!',
      showExplanation: 'Show explanation',
      hideExplanation: 'Hide explanation',
      previous: 'Previous',
      next: 'Next Question',
      finish: 'Finish Quiz',
      results: 'Quiz Results',
      score: 'Score',
      answered: 'You answered',
      outOf: 'out of',
      questionsCorrectly: 'questions correctly.',
      excellent: 'Excellent work! You have a strong understanding of Computer Architecture.',
      good: 'Good job! You have a solid grasp of Computer Architecture concepts.',
      keepStudying: 'Keep studying! Review the chapters to improve your understanding.',
      restart: 'Restart Quiz',
      createdBy: 'Created by',
      switchToArabic: 'العربية',
      emptyChapter: 'This chapter has no questions yet.',
      chapterResults: 'Chapter Results',
      chapterCompleted: 'Chapter Completed!',
      reviewAnswers: 'Review Your Answers',
      continueToNextChapter: 'Continue to Next Chapter',
      backToChapters: 'Back to Chapters',
      yourAnswer: 'Your Answer',
      correctAnswer: 'Correct Answer',
      alreadyAnswered: 'Already Answered'
    },
    ar: {
      title: 'اختبار هندسة الكمبيوتر',
      question: 'سؤال',
      of: 'من',
      correct: 'صحيح!',
      incorrect: 'غير صحيح!',
      showExplanation: 'عرض التفسير',
      hideExplanation: 'إخفاء التفسير',
      previous: 'السابق',
      next: 'السؤال التالي',
      finish: 'إنهاء الاختبار',
      results: 'نتائج الاختبار',
      score: 'النتيجة',
      answered: 'لقد أجبت على',
      outOf: 'من أصل',
      questionsCorrectly: 'أسئلة بشكل صحيح.',
      excellent: 'عمل ممتاز! لديك فهم قوي لهندسة الكمبيوتر.',
      good: 'عمل جيد! لديك فهم جيد لمفاهيم هندسة الكمبيوتر.',
      keepStudying: 'واصل الدراسة! راجع الفصول لتحسين فهمك.',
      restart: 'إعادة الاختبار',
      createdBy: 'تم إنشاؤه بواسطة',
      switchToArabic: 'English',
      emptyChapter: 'هذا الفصل لا يحتوي على أسئلة بعد.',
      chapterResults: 'نتائج الفصل',
      chapterCompleted: 'تم إكمال الفصل!',
      reviewAnswers: 'مراجعة إجاباتك',
      continueToNextChapter: 'الاستمرار إلى الفصل التالي',
      backToChapters: 'العودة إلى الفصول',
      yourAnswer: 'إجابتك',
      correctAnswer: 'الإجابة الصحيحة',
      alreadyAnswered: 'تم الإجابة بالفعل'
    }
  };

  const t = translations[language];

  // Direction based on language
  const dir = language === 'ar' ? 'rtl' : 'ltr';

  if (showChapterResults) {
    return (
      <ChapterResults 
        userAnswers={userAnswers}
        chapterName={currentChapter}
        score={score}
        totalQuestions={currentQuestions.length}
        language={language}
        darkMode={darkMode}
        toggleDarkMode={toggleDarkMode}
        toggleLanguage={toggleLanguage}
        continueToNextChapter={continueToNextChapter}
        goToChapters={goToChapters}
        translations={t}
        dir={dir}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-300" dir={dir}>
      <header className="bg-white dark:bg-gray-800 shadow-md">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-primary-600 dark:text-primary-400">
            {t.title}
          </h1>
          <div className="flex items-center space-x-4 rtl:space-x-reverse">
            <button
              onClick={goToChapters}
              className="px-3 py-1 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600"
            >
              {t.backToChapters}
            </button>
            <button
              onClick={toggleLanguage}
              className="px-3 py-1 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600"
            >
              {t.switchToArabic}
            </button>
            <button
              onClick={toggleDarkMode}
              className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300"
              aria-label="Toggle dark mode"
            >
              {darkMode ? (
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
              ) : (
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
              )}
            </button>
          </div>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        {!showResults ? (
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 max-w-4xl mx-auto">
            <div className="mb-4 flex justify-between items-center">
              <h2 className="text-xl font-semibold text-gray-800 dark:text-white">
                {currentChapter}
              </h2>
              {currentQuestions.length > 0 && (
                <div className="text-sm text-gray-600 dark:text-gray-400">
                  {t.question} {currentQuestionIndex + 1} {t.of} {currentQuestions.length}
                  {isQuestionAnswered && (
                    <span className="ml-2 rtl:mr-2 rtl:ml-0 text-blue-600 dark:text-blue-400">
                      ({t.alreadyAnswered})
                    </span>
                  )}
                </div>
              )}
            </div>

            {currentQuestions.length > 0 && currentQuestion ? (
              <div>
                <div className="mb-8">
                  <h3 className="text-lg font-medium text-gray-800 dark:text-white mb-4">
                    {currentQuestion.question}
                  </h3>

                  <div className="space-y-3">
                    {currentQuestion.options.map((option, index) => (
                      <button
                        key={index}
                        onClick={() => handleOptionSelect(index)}
                        className={`w-full text-left p-4 rounded-lg border ${
                          selectedOption === null
                            ? 'border-gray-300 dark:border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700'
                            : selectedOption === index
                              ? index === currentQuestion.correct
                                ? 'border-green-500 bg-green-100 dark:bg-green-900/30'
                                : 'border-red-500 bg-red-100 dark:bg-red-900/30'
                              : index === currentQuestion.correct
                                ? 'border-green-500 bg-green-100 dark:bg-green-900/30'
                                : 'border-gray-300 dark:border-gray-600'
                        } transition-colors duration-200 ${
                          isQuestionAnswered ? 'cursor-not-allowed opacity-75' : 'cursor-pointer'
                        }`}
                        disabled={isQuestionAnswered}
                      >
                        <div className="flex items-start">
                          <div className={`flex-shrink-0 h-6 w-6 rounded-full flex items-center justify-center mr-3 rtl:ml-3 rtl:mr-0 ${
                            selectedOption === null
                              ? 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
                              : selectedOption === index
                                ? index === currentQuestion.correct
                                  ? 'bg-green-500 text-white'
                                  : 'bg-red-500 text-white'
                                : index === currentQuestion.correct
                                  ? 'bg-green-500 text-white'
                                  : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
                          }`}>
                            {String.fromCharCode(65 + index)}
                          </div>
                          <span className={`${
                            selectedOption !== null && (selectedOption === index || index === currentQuestion.correct)
                              ? 'font-medium'
                              : ''
                          } text-gray-800 dark:text-white`}>
                            {option}
                          </span>
                        </div>
                      </button>
                    ))}
                  </div>

                  {selectedOption !== null && (
                    <div className="mt-6">
                      <button
                        onClick={() => setShowExplanation(!showExplanation)}
                        className="text-primary-600 dark:text-primary-400 hover:underline"
                      >
                        {showExplanation ? t.hideExplanation : t.showExplanation}
                      </button>
                      {showExplanation && (
                        <p className="mt-4 text-gray-700 dark:text-gray-300">
                          {currentQuestion.explanation}
                        </p>
                      )}
                    </div>
                  )}
                </div>

                <div className="flex justify-between mt-8">
                  <button
                    onClick={handlePrevQuestion}
                    disabled={currentQuestionIndex === 0}
                    className="px-6 py-3 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600 disabled:opacity-50"
                  >
                    {t.previous}
                  </button>
                  <button
                    onClick={handleNextQuestion}
                    disabled={!isQuestionAnswered && currentQuestionIndex < currentQuestions.length - 1}
                    className="px-6 py-3 rounded-md bg-primary-600 text-white hover:bg-primary-700 disabled:opacity-50"
                  >
                    {currentQuestionIndex < currentQuestions.length - 1 ? t.next : t.finish}
                  </button>
                </div>
              </div>
            ) : (
              <p className="text-gray-700 dark:text-gray-300 text-center">
                {t.emptyChapter}
              </p>
            )}
          </div>
        ) : (
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 max-w-4xl mx-auto text-center">
            <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-4">
              {t.results}
            </h2>
            <p className="text-xl text-gray-700 dark:text-gray-300 mb-2">
              {t.score}: {score} / {currentQuestions.length}
            </p>
            <p className="text-lg text-gray-600 dark:text-gray-400 mb-6">
              {t.answered} {score} {t.outOf} {currentQuestions.length} {t.questionsCorrectly}
            </p>

            {/* Feedback based on score */}
            {score / currentQuestions.length >= 0.8 ? (
              <p className="text-green-600 dark:text-green-400 font-semibold">
                {t.excellent}
              </p>
            ) : score / currentQuestions.length >= 0.5 ? (
              <p className="text-yellow-600 dark:text-yellow-400 font-semibold">
                {t.good}
              </p>
            ) : (
              <p className="text-red-600 dark:text-red-400 font-semibold">
                {t.keepStudying}
              </p>
            )}

            <div className="mt-8 flex justify-center space-x-4 rtl:space-x-reverse">
              <button
                onClick={restartQuiz}
                className="px-6 py-3 rounded-md bg-primary-600 text-white hover:bg-primary-700"
              >
                {t.restart}
              </button>
              <button
                onClick={goToChapters}
                className="px-6 py-3 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600"
              >
                {t.backToChapters}
              </button>
            </div>
          </div>
        )}
      </main>

      <footer className="bg-white dark:bg-gray-800 shadow-md mt-8 py-4 text-center text-gray-600 dark:text-gray-400">
        <p>
          {t.createdBy} Abdelhmeed Elshorbagy
        </p>
        <div className="flex justify-center space-x-4 mt-2">
          <a href="https://wa.me/96893637398" target="_blank" rel="noopener noreferrer" className="hover:text-primary-600 dark:hover:text-primary-400">
            WhatsApp
          </a>
          <a href="https://www.linkedin.com/in/abameda" target="_blank" rel="noopener noreferrer" className="hover:text-primary-600 dark:hover:text-primary-400">
            LinkedIn
          </a>
          <a href="https://www.instagram.com/abamedax/" target="_blank" rel="noopener noreferrer" className="hover:text-primary-600 dark:hover:text-primary-400">
            Instagram
          </a>
          <a href="https://abameda.github.io/Elshorbagy/" target="_blank" rel="noopener noreferrer" className="hover:text-primary-600 dark:hover:text-primary-400">
            Portfolio
          </a>
        </div>
      </footer>
    </div>
  );
};

export default Quiz;
