import React, { useState, useEffect, useCallback } from 'react';
import { quizData, quizDataArabic, chapterMapping, reverseChapterMapping } from '../data/questions';
import ChapterResults from './ChapterResults'; // Assuming this component exists

const Quiz = () => {
  const [darkMode, setDarkMode] = useState(false);
  const [language, setLanguage] = useState('en'); // 'en' for English, 'ar' for Arabic
  const [currentChapter, setCurrentChapter] = useState('');
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [answeredQuestions, setAnsweredQuestions] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showChapterResults, setShowChapterResults] = useState(false);

  // Get the appropriate quiz data based on language
  const currentQuizData = language === 'en' ? quizData : quizDataArabic;
  const t = translations[language]; // Current translation object
  const dir = language === 'ar' ? 'rtl' : 'ltr'; // Text direction

  // Derived state for current questions and question
  const currentQuestions = currentChapter && currentQuizData[currentChapter] ? currentQuizData[currentChapter] : [];
  const currentQuestion = currentQuestions.length > 0 ? currentQuestions[currentQuestionIndex] : null;
  const totalQuestions = currentQuestions.length;

  // --- Initial Mount / Load from Local Storage ---
  useEffect(() => {
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setDarkMode(savedDarkMode);
    if (savedDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }

    const savedLanguage = localStorage.getItem('language');
    if (savedLanguage) {
      setLanguage(savedLanguage);
    }
  }, []);

  // --- Effect to manage chapter and language changes ---
  useEffect(() => {
    console.log('--- Language/Chapter Effect Running ---');
    console.log('Current Language:', language);
    console.log('currentChapter (before update logic):', currentChapter);
    console.log('currentQuizData keys:', Object.keys(currentQuizData));

    let chapterToLoad = '';
    const savedChapter = localStorage.getItem('selectedChapter'); // From Home page

    if (savedChapter) {
      console.log('Found savedChapter in localStorage:', savedChapter);
      // Attempt to map the saved chapter to the current language
      if (language === 'en' && reverseChapterMapping[savedChapter]) {
        chapterToLoad = reverseChapterMapping[savedChapter];
        console.log(`Mapped saved Arabic chapter '${savedChapter}' to English: '${chapterToLoad}'`);
      } else if (language === 'ar' && chapterMapping[savedChapter]) {
        chapterToLoad = chapterMapping[savedChapter];
        console.log(`Mapped saved English chapter '${savedChapter}' to Arabic: '${chapterToLoad}'`);
      } else {
        // If the saved chapter is already in the target language or no specific mapping
        chapterToLoad = savedChapter;
        console.log(`Used savedChapter directly as no mapping needed or found for current language: '${chapterToLoad}'`);
      }
      localStorage.removeItem('selectedChapter'); // Clear after use
    } else if (currentChapter) {
      console.log('No new savedChapter, but currentChapter exists:', currentChapter);
      // If language changes mid-quiz, try to map the current chapter
      if (language === 'ar' && chapterMapping[currentChapter]) {
        chapterToLoad = chapterMapping[currentChapter];
        console.log(`Mapped current English chapter '${currentChapter}' to Arabic: '${chapterToLoad}'`);
      } else if (language === 'en' && reverseChapterMapping[currentChapter]) {
        chapterToLoad = reverseChapterMapping[currentChapter];
        console.log(`Mapped current Arabic chapter '${currentChapter}' to English: '${chapterToLoad}'`);
      } else {
        // If no direct mapping (e.g., chapter name is the same in both languages, or not found in map)
        chapterToLoad = currentChapter;
        console.log(`Used currentChapter directly (no mapping needed or found for current language): '${chapterToLoad}'`);
      }
    } else {
      console.log('No savedChapter and no currentChapter. Attempting to get first chapter.');
      // If no saved chapter and no current chapter, pick the first available in the current language
      const chapters = Object.keys(currentQuizData);
      if (chapters.length > 0) {
        chapterToLoad = chapters[0];
        console.log('Defaulted to first available chapter:', chapterToLoad);
      } else {
        console.warn('No chapters found in currentQuizData for language:', language);
      }
    }

    console.log('chapterToLoad determined:', chapterToLoad);
    const chapterExistsInCurrentQuizData = currentQuizData.hasOwnProperty(chapterToLoad) && currentQuizData[chapterToLoad].length > 0;
    console.log(`Does currentQuizData have this chapter and questions? ${chapterExistsInCurrentQuizData ? 'Yes' : 'No'}`);

    // Update state only if necessary
    if (chapterToLoad && chapterToLoad !== currentChapter) {
      setCurrentChapter(chapterToLoad);
      console.log('Setting currentChapter to:', chapterToLoad);
      // Reset all quiz related states
      setCurrentQuestionIndex(0);
      setSelectedOption(null);
      setShowExplanation(false);
      setScore(0);
      setUserAnswers([]);
      setAnsweredQuestions(0);
      setQuizCompleted(false);
      setShowResults(false);
      setShowChapterResults(false);
      console.log('Quiz state reset due to chapter change.');
    } else if (!currentChapter && chapterToLoad) {
      // Handle initial load if currentChapter is empty and we found a chapterToLoad
      setCurrentChapter(chapterToLoad);
      console.log('Initial load: Setting currentChapter to:', chapterToLoad);
    } else if (chapterToLoad === currentChapter && currentChapter && !chapterExistsInCurrentQuizData) {
      // Edge case: current chapter is set, but it became empty after language switch
      console.warn(`Current chapter '${currentChapter}' has no questions after language switch.`);
      // You might want to force a home redirection or default to first chapter here.
    } else if (!chapterToLoad) {
      console.warn('chapterToLoad is empty. This might lead to "no questions added".');
    }

  }, [language, currentQuizData]); // Dependencies: language and currentQuizData for re-evaluation

  // --- Effect to log when currentQuestions changes length ---
  useEffect(() => {
    console.log(`currentQuestions updated. Length: ${currentQuestions.length}`);
    if (currentQuestions.length === 0 && currentChapter) {
      console.error(`Error: Chapter "${currentChapter}" has no questions in the current language's quiz data.`);
    }
  }, [currentQuestions, currentChapter]); // Dependencies: currentQuestions, currentChapter

  const toggleDarkMode = useCallback(() => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());
    document.documentElement.classList.toggle('dark', newDarkMode);
  }, [darkMode]);

  const toggleLanguage = useCallback(() => {
    const newLanguage = language === 'en' ? 'ar' : 'en';
    console.log('Toggling language to:', newLanguage);
    setLanguage(newLanguage);
    localStorage.setItem('language', newLanguage);
    // The language useEffect will handle chapter mapping and state resets
  }, [language]);

  const handleChapterChange = useCallback((chapterName) => {
    setCurrentChapter(chapterName);
    setCurrentQuestionIndex(0);
    setSelectedOption(null);
    setShowExplanation(false);
    setScore(0);
    setQuizCompleted(false);
    setShowResults(false);
    setAnsweredQuestions(0);
    setUserAnswers([]);
    setShowChapterResults(false);
  }, []);

  const handleOptionSelect = useCallback((optionIndex) => {
    if (selectedOption === null) { // Only allow selection if not already selected
      setSelectedOption(optionIndex);
      setShowExplanation(true); // Always show explanation after selection

      const isCorrect = optionIndex === currentQuestion.correct;
      if (isCorrect) {
        setScore((prevScore) => prevScore + 1);
      }

      setUserAnswers((prevAnswers) => [
        ...prevAnswers,
        {
          questionIndex: currentQuestionIndex,
          selectedOption: optionIndex,
          isCorrect: isCorrect,
          question: currentQuestion.question,
          options: currentQuestion.options,
          correctAnswer: currentQuestion.correct,
          explanation: currentQuestion.explanation,
        },
      ]);
      setAnsweredQuestions((prev) => prev + 1);
    }
  }, [selectedOption, currentQuestion, currentQuestionIndex]);

  const handleNextQuestion = useCallback(() => {
    if (currentQuestionIndex < currentQuestions.length - 1) {
      setCurrentQuestionIndex((prevIndex) => prevIndex + 1);
      setSelectedOption(null);
      setShowExplanation(false);
    } else {
      setQuizCompleted(true);
      setShowResults(true);
      setShowChapterResults(true); // Show ChapterResults when quiz is completed
    }
  }, [currentQuestionIndex, currentQuestions.length]);

