    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex((prevIndex) => prevIndex - 1);
      setSelectedOption(null); // Clear selected option for previous question
      setShowExplanation(false); // Hide explanation for previous question
      // Note: Score and userAnswers are not reverted on prev,
      // as they track the overall quiz performance.
    }
  }, [currentQuestionIndex]);

